package auth

import (
	"net/http"
	"strings"
	"time"

	"github.com/gin-gonic/gin"
	"github.com/golang-jwt/jwt/v4"
	"github.com/hthinh24/go-store/internal/pkg/logger"
	"github.com/hthinh24/go-store/internal/pkg/rest"
)

type SharedAuthMiddleware struct {
	logger    logger.Logger
	jwtSecret string
}

type JWTClaims struct {
	UserID           int64     `json:"user_id"`
	Email            string    `json:"email"`
	Roles            []string  `json:"roles"`
	Permissions      []string  `json:"permissions"`
	RefreshExpiresAt time.Time `json:"refresh_expires_at"`
	jwt.RegisteredClaims
}

func NewSharedAuthMiddleware(logger logger.Logger, jwtSecret string) *SharedAuthMiddleware {
	return &SharedAuthMiddleware{
		logger:    logger,
		jwtSecret: jwtSecret,
	}
}

// AuthRequired validates JWT token and sets user info in context
func (m *SharedAuthMiddleware) AuthRequired() gin.HandlerFunc {
	return func(c *gin.Context) {
		authHeader := c.GetHeader("Authorization")
		if authHeader == "" {
			c.JSON(http.StatusUnauthorized, rest.ErrorResponse{
				ApiError: rest.UnauthorizedError,
				Message:  "Authorization header required",
			})
			c.Abort()
			return
		}

		tokenString := strings.TrimPrefix(authHeader, "Bearer ")
		if tokenString == authHeader {
			c.JSON(http.StatusUnauthorized, rest.ErrorResponse{
				ApiError: rest.UnauthorizedError,
				Message:  "Bearer token required",
			})
			c.Abort()
			return
		}

		claims, err := m.validateToken(tokenString)
		if err != nil {
			m.logger.Error("Failed to validate JWT token", "error", err)
			c.JSON(http.StatusUnauthorized, rest.ErrorResponse{
				ApiError: rest.UnauthorizedError,
				Message:  "Invalid token",
			})
			c.Abort()
			return
		}

		// Set user info in context for all services to use
		c.Set("user_id", claims.UserID)
		c.Set("email", claims.Email)
		c.Set("roles", claims.Roles)
		c.Set("permissions", claims.Permissions)

		c.Next()
	}
}

// RequirePermissions checks if user has ALL specified permissions
func (m *SharedAuthMiddleware) RequirePermissions(requiredPermissions ...string) gin.HandlerFunc {
	return func(c *gin.Context) {
		permissions, exists := c.Get("permissions")
		if !exists {
			c.JSON(http.StatusUnauthorized, rest.ErrorResponse{
				ApiError: rest.UnauthorizedError,
				Message:  "User not authenticated",
			})
			c.Abort()
			return
		}

		userPermissions := permissions.([]string)
		userPermMap := make(map[string]bool)
		for _, perm := range userPermissions {
			userPermMap[perm] = true
		}

		// Check if user has all required permissions
		for _, requiredPerm := range requiredPermissions {
			if !userPermMap[requiredPerm] {
				m.logger.Warn("Access denied - missing permission", 
					"user_id", c.Get("user_id"), 
					"required_permission", requiredPerm)
				c.JSON(http.StatusForbidden, rest.ErrorResponse{
					ApiError: rest.ForbiddenError,
					Message:  "Insufficient permissions",
				})
				c.Abort()
				return
			}
		}

		c.Next()
	}
}

// RequireAnyPermission checks if user has ANY of the specified permissions
func (m *SharedAuthMiddleware) RequireAnyPermission(permissions ...string) gin.HandlerFunc {
	return func(c *gin.Context) {
		userPermissions, exists := c.Get("permissions")
		if !exists {
			c.JSON(http.StatusUnauthorized, rest.ErrorResponse{
				ApiError: rest.UnauthorizedError,
				Message:  "User not authenticated",
			})
			c.Abort()
			return
		}

		userPerms := userPermissions.([]string)
		for _, userPerm := range userPerms {
			for _, requiredPerm := range permissions {
				if userPerm == requiredPerm {
					c.Next()
					return
				}
			}
		}

		c.JSON(http.StatusForbidden, rest.ErrorResponse{
			ApiError: rest.ForbiddenError,
			Message:  "Insufficient permissions",
		})
		c.Abort()
	}
}

// RequireRole checks if user has specific role
func (m *SharedAuthMiddleware) RequireRole(role string) gin.HandlerFunc {
	return func(c *gin.Context) {
		roles, exists := c.Get("roles")
		if !exists {
			c.JSON(http.StatusUnauthorized, rest.ErrorResponse{
				ApiError: rest.UnauthorizedError,
				Message:  "User not authenticated",
			})
			c.Abort()
			return
		}

		userRoles := roles.([]string)
		for _, userRole := range userRoles {
			if userRole == role {
				c.Next()
				return
			}
		}

		c.JSON(http.StatusForbidden, rest.ErrorResponse{
			ApiError: rest.ForbiddenError,
			Message:  "Insufficient role permissions",
		})
		c.Abort()
	}
}

func (m *SharedAuthMiddleware) validateToken(tokenString string) (*JWTClaims, error) {
	token, err := jwt.ParseWithClaims(tokenString, &JWTClaims{}, func(token *jwt.Token) (interface{}, error) {
		return []byte(m.jwtSecret), nil
	})
	if err != nil {
		return nil, err
	}

	claims, ok := token.Claims.(*JWTClaims)
	if !ok {
		return nil, jwt.ErrTokenMalformed
	}

	now := time.Now()

	// Check if refresh token is expired (hard expiration)
	if !claims.RefreshExpiresAt.IsZero() && now.After(claims.RefreshExpiresAt) {
		m.logger.Warn("Refresh token has expired", "user_id", claims.UserID)
		return nil, jwt.NewValidationError("refresh token expired", jwt.ValidationErrorExpired)
	}

	// Check if access token is expired
	if claims.ExpiresAt != nil && now.After(claims.ExpiresAt.Time) {
		m.logger.Info("Access token expired", "user_id", claims.UserID)
		return nil, jwt.NewValidationError("access token expired", jwt.ValidationErrorExpired)
	}

	// Check not before
	if claims.NotBefore != nil && now.Before(claims.NotBefore.Time) {
		return nil, jwt.NewValidationError("token used before valid", jwt.ValidationErrorNotValidYet)
	}

	// Check issued at
	if claims.IssuedAt != nil && now.Before(claims.IssuedAt.Time) {
		return nil, jwt.NewValidationError("token issued in the future", jwt.ValidationErrorIssuedAt)
	}

	return claims, nil
}
